mapper.select:
SELECT b.id, b.author, b.content, b.created_at, b.dead_line, b.image_url, b.publish_date, b.publisher, b.recommend_count, b.return_location, b.review, b.title, b.updated_at, b.category_id, b.company_id, b.member_id, b.rental_use_yn, c.book_category FROM book b INNER JOIN category c on c.id = b.category_id WHERE b.company_id = #{companyId}
AND c.book_category = #{rentalBook.bookCategory}
AND b.title LIKE CONCAT('%', #{rentalBook.searchKeyword}, '%')
ORDER BY b.created_at DESC
ORDER BY b.recommend_count DESC
ORDER BY b.id DESC
limit #{rentalBook.size} OFFSET #{offset}

SELECT count(*) FROM book b INNER JOIN category c ON c.id = b.category_id WHERE b.company_id = #{companyId}
AND c.book_category = #{rentalBook.bookCategory}
AND b.title LIKE CONCAT('%', #{rentalBook.searchKeyword}, '%')
